    fun getSearchData(): List<Recipe> {
        val dtoMapper = RecipeDtoMapper()
        return dtoMapper.toDomainList(
            Json{
                isLenient = true
                ignoreUnknownKeys = true
            }.decodeFromString<RecipeSearchResponse>(recipeListResponse).results
        )
    }

